mapper.select:
SELECT t_order.order_id, t_order.order_code, t_order.order_condition, t_order.order_total_price, t_order_detail.order_detail_id, t_order_detail.order_detail_goods_code, t_order_detail.order_detail_goods_name, (select image_url from t_image_info where t_image_info.image_cate_code = t_order_detail.order_detail_goods_code) as image_url, (select goods_description from t_goods_info where t_goods_info.goods_id = t_order_detail.order_detail_goods_code) as goods_description, t_order_detail.order_detail_goods_sale_price, t_order_detail.order_detail_goods_num, t_sys_user_info.user_name, t_sys_user_info.user_phone, (select sum(order_detail_goods_num) from t_order_detail where t_order_detail.order_detail_order_code = t_order.order_id) as order_total_num from t_order LEFT JOIN t_order_detail on t_order.order_id = t_order_detail.order_detail_order_code LEFT JOIN t_sys_user_info on t_order.order_client_code = t_sys_user_info.user_id where t_order.order_store_code = (SELECT store_id from t_store_info where t_store_info.store_manager_id = #{userId})
and t_order.order_condition in (4, 5)
and t_order.order_condition = #{orderCondition}
